sys_script_include.script:
var AttachmentUtils = Class.create();
AttachmentUtils.prototype = {
    initialize: function() {
    },
    createProcessAttachment: function(filename, processSysId, payload) {
        var grp = new GlideRecordSecure("sn_vul_integration_process");

        grp.addQuery("sys_id", "=", processSysId + "");

        grp.query();

        var attachmentId;

        while (grp.next()) {
            var gsa = new GlideSysAttachment();
            attachmentId = gsa.write(grp, filename, 'application/json', payload);
        }

        return attachmentId;
    },
    readAttachment: function(attachmentId){
        var attachGr = new GlideRecordSecure('sys_attachment');

        if(attachGr.get(attachmentId)) {
            var sa = new GlideSysAttachment();
            return JSON.parse(sa.getContent(attachGr));
        }
    },
    readProcessAttachment: function(processSysId){
        var attachGr = new GlideRecordSecure('sys_attachment');
        attachGr.addQuery('table_sys_id', processSysId);
        attachGr.query();

        if(attachGr.next()) {
            var sa = new GlideSysAttachment();
            return JSON.parse(sa.getContent(attachGr));
        }
    },

    type: 'AttachmentUtils'
};
