sys_script_include.script:
var AquaServerTest = Class.create();
AquaServerTest.prototype = {
    initialize: function(apiUrl) {
        this.apiUrl = apiUrl;
    },
    _removeAllRecords: function(table) {
        var gr = new GlideRecord(table);
        gr.query();
        while (gr.next()) {
            //Delete each record in the query result set
            gr.deleteRecord();
        }
    },
    importCleanUp: function() {
        var aquaIntegrationRecord = gs.getProperty("x_aqss_vr_intgrtn.main.integration.record");

        var integrationGr = new GlideRecord("x_aqss_vr_intgrtn_x_aqss_vr_integration");

        if (integrationGr.get(aquaIntegrationRecord) && integrationGr.sys_id) {
            integrationGr.setValue("download_record_since", undefined);
            integrationGr.update();
        }


        //db_ci_docker_image
        this._removeAllRecords('cmdb_ci_docker_image');

        //cmdb_ci_container_repository
        this._removeAllRecords('cmdb_ci_container_repository');

        //this._removeAllRecords('sn_vul_nvd_entry');

        this._removeAllRecords('sn_vul_third_party_entry');

		this._removeAllRecords('sn_vul_container_image_package');

		this._removeAllRecords('sn_vul_container_image_findings');

		this._removeAllRecords('sn_vul_container_image');

        this._removeAllRecords('sn_vul_container_image_vulnerable_item');

        this._removeAllRecords('x_aqss_vr_intgrtn_aqua_security_scopes');
    },
    type: 'AquaServerTest'
};
