sys_script_include.script:
var AquaSecConfig = Class.create();
AquaSecConfig.prototype = {
    initialize: function() {
    },
    getDefaultInstanceId : function() {
        var integrationId = gs.getProperty("x_aqss_vr_intgrtn.integration.id");

        var implementationGr = new GlideRecordSecure("sn_sec_int_impl");
        implementationGr.addQuery("integration", integrationId);
        implementationGr.setLimit(1);
        implementationGr.query();

        if(implementationGr.next()) {
            return implementationGr.sys_id;
        } else {
            return undefined;
        }

    },
    getDefaultInstanceConfig: function() {
        var instanceId = this.getDefaultInstanceId();
        if (instanceId) {
            return new sn_sec_int.Implementation().getConfiguration(instanceId, false, false);
        } else {
            return {};
        }
    },
    setDefaultInstanceConfig: function(configs){
        var instanceId = this.getDefaultInstanceId();

        if(instanceId) {
            new sn_sec_int.Implementation().setConfiguration(instanceId, configs);
        }
    },
    getAutocloseSupported: function() {
        var integrationId = gs.getProperty("x_aqss_vr_intgrtn.integration.id");
        var integrationGr = new GlideRecord("sn_sec_int_integration");
        if (integrationGr.get(integrationId)) {
            return integrationGr.is_auto_close_supported;
        }
        return false;
    },
    setAutocloseSupported: function(value) {
        var integrationId = gs.getProperty("x_aqss_vr_intgrtn.integration.id");
        var integrationGr = new GlideRecord("sn_sec_int_integration");

        if (integrationGr.get(integrationId)) {
            integrationGr.setValue("is_auto_close_supported", value);
            integrationGr.update();
        }
    },
    type: 'AquaSecConfig'
};
