sys_script_include.script:
var MockAquaSecAPI = Class.create();
MockAquaSecAPI.prototype = {
    initialize: function(maxImages, maxVulns) {
        this.maxImages = maxImages;
        this.maxVulns = maxVulns;
    },
	login: function(username, password) {
	},

    getImages: function(registry, repository, updatedIn, appScope, page, pagesize) {
        var images = null;
        var limit =  this.maxImages >= page*pagesize ? pagesize : this.maxImages - (page-1)*pagesize;
        for (var i=1; i<=limit; i++){
            if (i == 1) {
                images = [];
            }
            images.push({digest: "sha256:image_"+ page + "_" + i+"("+repository+")"});
        }

        return {
            hasMore: this.maxImages > (page)*pagesize,
            items: images
        };
    },

    getRegistries: function() {
        var registries = [];
        for (var i=1; i<=5; i++){
            registries.push({name: "registry_"+ i});
        }

        return registries;
    },


    getVulnerabilities: function(registry, digest, page, pagesize) {
        var vulns = [];
        var limit =  this.maxVulns >= page*pagesize ? pagesize : this.maxVulns - (page-1)*pagesize;
        for (var i=1; i<=limit; i++){
            vulns.push({
                vulnerability: "vuln_" + page + "_" + i,
                dockerImage: digest + " (" + registry +")",
            });
        }

        return {
            hasMore: this.maxVulns > (page)*pagesize,
            items: vulns
        };


    },


    type: 'MockAquaSecAPI'
};
